import React from "react";

import Transaction from "./Transaction";

function AddTransactionForm() {
  const [date, setDate] = useInput("");
  const [description, setDescription] = useInput("");
  const [category, setCategory] = useInput("");
  const [amount, setAmount] = useInput("");

  function handleSubmit(e) {
    e.preventDefault();
    fetch("http://localhost:8001/transactions", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        date,
        description,
        category,
        amount
      })
    }).then(() => {
      alert("Added successfully");
      // Clear form after successful submission
      setDate("");
      setDescription("");
      setCategory("");
      setAmount("");
    });
  }

  return (
    <div className="ui segment">
      <form onSubmit={handleSubmit} className="ui form">
        <div className="inline fields">
          <input {...setDate} type="date" name="date" />
          <input {...setDescription} type="text" name="description" placeholder="Description" />
          <input {...setCategory} type="text" name="category" placeholder="Category" />
          <input {...setAmount} type="number" name="amount" placeholder="Amount" step="0.01" />
        </div>
        <button className="ui button" type="submit">
          Add Transaction
        </button>
      </form>
    </div>
  );
}

export default AddTransactionForm;
