import React, { useState } from "react";
import Transaction from "./Transaction";

function AddTransactionForm() {
  const [date, setDate] = useState("");
  const [description, setDescription] = useState("");
  const [category, setCategory] = useState("");
  const [amount, setAmount] = useState("");

  function handleDateChange(e) {
    setDate(e.target.value);
  }

  function handleDescriptionChange(e) {
    setDescription(e.target.value);
  }

  function handleCategoryChange(e) {
    setCategory(e.target.value);
  }

  function handleAmountChange(e) {
    setAmount(e.target.value);
  }

  function handleSubmit(e) {
    e.preventDefault();
    fetch("http://localhost:8001/transactions", {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({
        date,
        description,
        category,
        amount
      })
    }).then(() => {
      alert("Added successfully");
      // Clear form after successful submission
      setDate("");
      setDescription("");
      setCategory("");
      setAmount("");
    });
  }

  return (
    <div className="ui segment">
      <form onSubmit={handleSubmit} className="ui form">
        <div className="inline fields">
          <input value={date} onChange={handleDateChange} type="date" name="date" />
          <input value={description} onChange={handleDescriptionChange} type="text" name="description" placeholder="Description" />
          <input value={category} onChange={handleCategoryChange} type="text" name="category" placeholder="Category" />
          <input value={amount} onChange={handleAmountChange} type="number" name="amount" placeholder="Amount" step="0.01" />
        </div>
        <button className="ui button" type="submit">
          Add Transaction
        </button>
      </form>
    </div>
  );
}

export default AddTransactionForm;
